        <Container fluid>
          <Row>
            <Col size="md-6">
              <Card title="What Books Should I Read?">
                <form ref={formEl}>
                  <Input
                    onChange={handleInputChange}
                    name="title"
                    placeholder="Title (required)"
                  />
                  <Input
                    onChange={handleInputChange}
                    name="author"
                    placeholder="Author (required)"
                  />
                  <TextArea
                    onChange={handleInputChange}
                    name="synopsis"
                    placeholder="Synopsis (Optional)"
