import React, { useEffect, useState } from "react";
import API from "../../utils/API";

function SearchBar() {
  const [formObject, setFormObject] = useState({});

  function handleInputChange(event) {
    const { name, value } = event.target;
    setFormObject({ ...formObject, [name]: value });
  }

  function handleFormSubmit(event) {
    event.preventDefault();
    const search = formObject.search;
    API.findViaSearch(search).then((res) => {
      console.log(res);
    });
  }

  return (
    <div style={{ alignContent: "center "}}>
      <form>
        <div className="form-group">
          {/* <label htmlFor="exampleInputEmail1">Business Name</label> */}
          <input
            type="email"
            className="form-control"
            id="exampleInputEmail1"
            placeholder="Search for a business"
            aria-describedby="emailHelp"
            name="search"
            style={{ fontSize: 22, padding: 28, width: "35%" }}
            onChange={handleInputChange}
          />
          {/* <small id="emailHelp" className="form-text text-muted">
            Please search what you would like to find.
          </small> */}
        </div>
        <button
          type="submit"
          className="btn btn-primary"
          onClick={handleFormSubmit}
          style={ { padding: 18, fontSize: 22 } }
        >
          Submit
        </button>
      </form>
    </div>
  );
}
export default SearchBar;
